import React from "react";
import PropTypes from "prop-types";
import styles from "./Button.module.scss";

const Button = ({ type: Type, children, href }) => (
  <Type className={styles.button} type={Type} href={href ? href : null} target={target ? target  : null} rel={target === '_blank' ? "noopener noreferrer" : null}
        >
    {children}
  </Type>
);

Button.propTypes = {
  type: PropTypes.string.isRequired,
  href: PropTypes.string,
  target: PropTypes.string
};

Button.defaultProps = {
  href: null,
  target: null
};

export default Button;
